function svg_list()
{
	$SVG = new SVG;
	$svg_array[''] = 'NONE';
	$svg_array['sixty-seconds'] = 'SIXTY SECONDS RIGHT';
	foreach ($SVG as $key => $svg_val) {
		$svg_array[$key] = str_replace('_', ' ', strtoupper($key));
	}
	return $svg_array;
}

function action_admin_footer()
{
	$SVG = new SVG;
?>
	<style>
		<?php foreach ($SVG as $key => $svg_val) { ?>
			<?php $encodedSVG = \rawurlencode(\str_replace(["\r", "\n"], ' ', $svg_val)); ?>
				li[data-value="<?= $key ?>"], div[current-icon="<?= $key ?>"]:before {
					background-image: url('data:image/svg+xml, <?= $encodedSVG ?>');
				}
		<?php } ?>

		<?php $sixty_seconds = \rawurlencode(\str_replace(["\r", "\n"], ' ', $SVG->sixty_seconds)); ?>
		li[data-value="sixty-seconds"], div[current-icon="sixty-seconds"]:before {
			background-image: url('data:image/svg+xml, <?= $sixty_seconds ?>');
		}
	</style>
	<script>
		jQuery(document).ready(function() {
			jQuery('select[name$="button_icon]"], select[name$="_icon_svg]"]').niceSelect();

			jQuery('select[name$="button_icon]"], select[name$="_icon_svg]"]').each(function(index, element) {
				$val = jQuery(this).val();
				jQuery(this).next().attr('current-icon', $val);
			});
		});

		jQuery(document).on("change", 'select[name$="button_icon]"], select[name$="_icon_svg]"]', function() {
			$val = jQuery(this).val();
			jQuery(this).next().attr('current-icon', $val);
		});

		jQuery(document).on("change", 'select[name$="button_type]"]', function() {
			jQuery('select[name$="button_icon]"], select[name$="_icon_svg]"]').niceSelect();
		});
	</script>


<?php
}

add_action('admin_footer', 'action_admin_footer');
